
let burgerMenu = document.querySelector('.burger-menu');
let header =  document.querySelector('.header');


burgerMenu.addEventListener("click", function(){
    console.log(burgerMenu);

    burgerMenu.classList.contains('open-menu') ?
    burgerMenu.classList.remove('open-menu') :
    burgerMenu.classList.add('open-menu');

    header.classList.contains('open-menu') ?
    header.classList.remove('open-menu') :
    header.classList.add('open-menu');

});

let videoItem = document.querySelectorAll('.video-item');

videoItem.forEach(function(videoItem) {
    videoItem.addEventListener("mouseover", function(){
        this.classList.add('active');
    });
    videoItem.addEventListener("mouseleave", function(){
        setTimeout(() => this.classList.remove('active'), 5000);
    })

});


