
let burgerMenu = document.querySelector('.burger-menu');
let header =  document.querySelector('.header');


burgerMenu.addEventListener("click", function(){
    console.log(burgerMenu);

    burgerMenu.classList.contains('open-menu') ?
    burgerMenu.classList.remove('open-menu') :
    burgerMenu.classList.add('open-menu');

    header.classList.contains('open-menu') ?
    header.classList.remove('open-menu') :
    header.classList.add('open-menu');

});

let videoItem = document.querySelectorAll('.video-item');

videoItem.forEach(function(videoItem) {
    videoItem.addEventListener("mouseover", function(){
        this.classList.add('active');
    });
    videoItem.addEventListener("mouseleave", function(){
        setTimeout(() => this.classList.remove('active'), 5000);
    })

});


$(document).ready(function(){
    $(".owl-carousel").owlCarousel({
        loop:true,
        margin:10,
        nav:true,
        items: 4,
        responsive:{
            0:{
                items:1
            },
            600:{
                items:3
            },
            1000:{
                items:4
            }
        }
    });
});

  function owlInitialize() {
    if ($(window).width() < 780) {
            $('.owl-carousel-top').addClass("owl-carousel");
            $('.owl-carousel-top').owlCarousel({
                loop:false,         
                nav: true,
                // navText: ["<i class='fa fa-angle-left'></i>", "<i class='fa fa-angle-right'></i>"],
                dots: false,
                items: 1,
                autoHeight: false, 
                responsiveClass:true,
                responsive:{
                    0:{
                        items:1,
                        nav:true
                    }
                } ,
                // onChange: callBack  ,
                // onInitialize: callBack 
            });
            function callBack(){
                if($('.owl-carousel .owl-item').last().hasClass('active')){
                      $('.owl-next').hide();
                      $('.owl-prev').show(); 
                      console.log('true');
                   }else if($('.owl-carousel .owl-item').first().hasClass('active')){
                      $('.owl-prev').hide(); 
                      $('.owl-next').show();
                      console.log('false');
                   }
              }
            //   $('#owlCarousel .owl-prev').hide();
        }else{
            $('.owl-carousel-top').owlCarousel('destroy');
            $('.owl-carousel-top').removeClass("owl-carousel");
        }
    }

    // $('#owlCarousel .owl-prev').hide();
$(document).ready(function(e) {
        owlInitialize();
});
$(window).resize(function() {
        owlInitialize();
});


